local function createSendSync(shader, code, sync)
	local embedded = code.embedded
	local sent = {}
	for i = 0, 1 do
		local k = embedded[i]
		if k ~= 0 then
			table.insert(sent, tove._sent[k])
		end
	end
	if #sent == 0 then
		return sync
	else
		local version = 0
		local max = math.max
		return function()
			local nversion = version
			for _, s in ipairs(sent) do
				for name, values in pairs(s) do
					local v = values[1]
					if v > version then
						nversion = max(nversion, v)
						shader:send(name, unpack(values[2]))
					end
				end
			end
			version = nversion
			if sync ~= nil then
				sync()
			end
		end
	end
end

